const App = () => {
  const course = 'Half Stack application development'

  const parts = [
    {
      name: 'Fundamentals of React',
      exercises: 10
    },
    {
      name: 'Using props to pass data',
      exercises: 7
    },
    {
      name: 'State of a component',
      exercises: 14
    }
  ]

  const Header = (props) => {
  return (
    <h1>{props.course}</h1>
  )
  }

  const Part = (props) => {
  return (
    <p>
      {props.part} {props.exercises}
    </p>
  )
  }	  

  const Content = () => {
  console.log(parts)	  
  return (
    <div>
      parts.forEach(value => {
        <Part part={value.name} exercises={value.exercises}/>
      })                    	  
    </div>
  )
  }

  const Total = () => {
  let sumOfParts = 0;

  parts.forEach(element => {
  sumOfParts += element.exercises;
  });	  
  return (
    <p>Number of exercises {sumOfParts}</p>
  )	  
  }	  

  return (
    <div>
      <Header course={course} />
      <Content />	  
      <Total />
    </div>
  )
}

export default App
