const App = () => {
  const course = 'Half Stack application development'

  const parts = [
    {
      name: 'Fundamentals of React',
      exercises: 10
    },
    {
      name: 'Using props to pass data',
      exercises: 7
    },
    {
      name: 'State of a component',
      exercises: 14
    }
  ]

  const Header = (props) => {
  return (
    <h1>{props.course}</h1>
  )
  }

  const Part = (props) => {
  return (
    <p>
      {props.part} {props.exercises}
    </p>
  )
  }	  

  const Content = (props) => {
  console.log(props.parts)	  
  return (
    <div>
        <Part part={props.parts[0].name} exercises={props.parts[0].exercises}/>
    </div>
  )
  }

  const Total = (props) => {
  let sumOfParts = 0;

  props.parts.forEach(element => {
  sumOfParts += element.exercises;
  });	  
  return (
    <p>Number of exercises {sumOfParts}</p>
  )	  
  }	  

  return (
    <div>
      <Header course={course} />
      <Content parts={parts}/>	  
      <Total parts={parts}/>
    </div>
  )
}

export default App
